#!/usr/bin/env node
/**
 * Start the Mastra app in production mode
 *
 * Usage:
 *   npm run start:production
 *
 * Notes:
 *   - Requires prior build: `npm run build`
 *   - Loads .mastra/output files generated by Mastra
 */
import { spawn } from 'child_process';
import { fileURLToPath } from 'url';
import { dirname, join } from 'path';

const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);
const projectRoot = join(__dirname, '..');

console.log('Starting Mastra production server...');
console.log('Project root:', projectRoot);

// Change to project directory
process.chdir(projectRoot);

// Start the Mastra server with telemetry
const child = spawn('node', [
    '--import=./.mastra/output/instrumentation.mjs',
    '.mastra/output/index.mjs'
], {
    stdio: 'inherit',
    env: {
        ...process.env,
        NODE_ENV: 'production',
        PORT: process.env.PORT || '8080',
        HOST: process.env.HOST || '0.0.0.0'
    }
});

child.on('error', (error) => {
    console.error('Failed to start server:', error);
    process.exit(1);
});

child.on('exit', (code) => {
    console.log(`Server exited with code ${code}`);
    process.exit(code || 0);
});

// Handle graceful shutdown
process.on('SIGTERM', () => {
    console.log('Received SIGTERM, shutting down gracefully...');
    child.kill('SIGTERM');
});

process.on('SIGINT', () => {
    console.log('Received SIGINT, shutting down gracefully...');
    child.kill('SIGINT');
});